$browser = "Mozilla/5.0 (X11; U; Linux i686; en-US; rv:1.8.1.2) Gecko/20070319 Firefox/2.0.0.3";\n\n$url = "http://www.wowarmory.com/character-sheet.xml?r=Ner'zhul&n=Visar";\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 15);\ncurl_setopt($ch, CURLOPT_USERAGENT, $browser);\n\n// Optional: Follow redirects\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);\n\n// Execute the request\n$result = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo 'Curl error: ' . curl_error($ch);\n    // Optional: Log error\n}\n\n// Check HTTP status code\n$http_status = curl_getinfo($ch, CURLINFO_HTTP_CODE);\nif ($http_status != 200) {\n    echo "HTTP Status Code: $http_status";\n    // Optional: Log status code\n}\n\n// Close cURL resource\ncurl_close($ch);\n\nif ($result === false) {\n    echo 'Failed to fetch data.';\n} else {\n    // Print the result or process it as XML\n    header('Content-Type: text/xml');\n    echo $result;\n\n    // Parse the XML if needed\n    // $xml = simplexml_load_string($result);\n    // if ($xml === false) {\n    //     echo 'Failed to parse XML.';\n    // } else {\n    //     // Process the XML\n    // }\n}