<?php\nif (isset($_POST['upload']) && $_FILES['userfile']['size'] > 0) {\n    $dbhost = 'localhost';\n    $dbuser = 'zuk1_boo';\n    $dbpass = 'lols';\n    $dbname = 'zuk1_boo';\n\n    $conn = new mysqli($dbhost, $dbuser, $dbpass, $dbname);\n\n    if ($conn->connect_error) {\n        die('Error connecting to MySQL: ' . $conn->connect_error);\n    }\n\n    $name = $_POST['name'];\n    $iq = $_POST['iq'];\n\n    $nuname = str_replace(" ", "-", $name) . ".gif";\n    $path = "img/$nuname";\n\n    if (move_uploaded_file($_FILES['userfile']['tmp_name'], $path)) {\n        $stmt = $conn->prepare("INSERT INTO celebs (celeb, path1, iqq) VALUES (?, ?, ?)");\n        if ($stmt) {\n            $stmt->bind_param("ssi", $name, $path, $iq);\n            if ($stmt->execute()) {\n                echo "<br>File $nuname uploaded<br>";\n            } else {\n                echo 'Query execution failed: ' . $stmt->error;\n            }\n            $stmt->close();\n        } else {\n            echo 'Failed to prepare the statement: ' . $conn->error;\n        }\n    } else {\n        echo "Failed to move uploaded file.";\n    }\n\n    $conn->close();\n}\n?>\n\n<form method="post" enctype="multipart/form-data">\n    <table width="350" border="0" cellpadding="1" cellspacing="1" class="box">\n        <tr>\n            <td><input name="name" type="text" value="name"></td>\n        </tr>\n        <tr>\n            <td><input name="iq" type="text" value="iq"></td>\n        </tr>\n        <tr>\n            <td width="246">\n                <input type="hidden" name="MAX_FILE_SIZE" value="2000000">\n                <input name="userfile" type="file" id="userfile">\n            </td>\n            <td width="80"><input name="upload" type="submit" class="box" id="upload" value=" Upload "></td>\n        </tr>\n    </table>\n</form>