<?php\ntry {\n    $client = new SoapClient("http://example.com/soap?wsdl");\n\n    // Assuming 'select_query' is the method you are calling\n    $response = $client->__soapCall('select_query', array("param1" => "value1", "param2" => "value2"));\n    \n    // Process the response\n    print_r($response);\n} catch (SoapFault $fault) {\n    // Handle the SoapFault exception\n    echo "SoapFault exception: [" . $fault->faultcode . "] " . $fault->getMessage();\n    // Optionally log the fault details\n    error_log("SoapFault: " . $fault->getMessage());\n}\n?>