<?php\n// Your existing query\n$original_query = "SELECT * FROM products WHERE some_condition = true";\n\n// Wrap the original query and use it to select distinct product types\n$distinct_query = "\n    SELECT DISTINCT product_type \n    FROM (\n        $original_query\n    ) AS subquery\n";\n\n// Execute the distinct query\n$result = mysqli_query($connection, $distinct_query);\n\n// Fetch and display the distinct product types\n$product_types = array();\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $product_types[] = $row['product_type'];\n}\n\n// Use $product_types to populate your dropdown options\n?>