function is_user_online($user_id) {\n       $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n       $stmt = $pdo->prepare('SELECT last_activity FROM user_sessions WHERE user_id = ?');\n       $stmt->execute([$user_id]);\n       $result = $stmt->fetch(PDO::FETCH_ASSOC);\n\n       if ($result) {\n           // Check if the last activity was within the session timeout limit\n           $last_activity = strtotime($result['last_activity']);\n           $timeout_limit = time() - ini_get('session.gc_maxlifetime');\n           return $last_activity >= $timeout_limit;\n       }\n       return false;\n   }\n\n   // Example usage:\n   $user_id = 1; // Replace with the user ID you want to check\n   if (is_user_online($user_id)) {\n       echo "User is online";\n   } else {\n       echo "User is offline";\n   }