<?php\ninclude('ventrilostatus.php');\n\n$vent = new VentriloStatus();\n$vent->m_cmdprog = '/path/to/ventrilo_status'; // Path to the Ventrilo status binary\n$vent->m_cmdcode = '2'; // Command code for detail mode\n$vent->m_cmdhost = 'your_ventrilo_server_address'; // Your Ventrilo server address\n$vent->m_cmdport = '3784'; // Your Ventrilo server port\n$vent->m_cmdpass = ''; // Your Ventrilo server password if any\n\nif ($vent->Request() == 0) {\n    echo "<p>Server Name: " . htmlspecialchars($vent->m_name) . "</p>";\n    echo "<p>Server Phonetic: " . htmlspecialchars($vent->m_phonetic) . "</p>";\n    echo "<p>Server Comment: " . htmlspecialchars($vent->m_comment) . "</p>";\n    echo "<p>Server Uptime: " . htmlspecialchars($vent->m_uptime) . "</p>";\n    echo "<p>Channels:</p><ul>";\n\n    foreach ($vent->m_channel as $chan) {\n        echo "<li>Channel Name: " . htmlspecialchars($chan->m_name) . " (" . htmlspecialchars($chan->m_clients) . " clients)</li>";\n    }\n\n    echo "</ul><p>Clients:</p><ul>";\n\n    foreach ($vent->m_client as $cli) {\n        echo "<li>Client Name: " . htmlspecialchars($cli->m_name) . " in Channel " . htmlspecialchars($cli->m_channel) . "</li>";\n    }\n\n    echo "</ul>";\n} else {\n    echo "<p>Error: Unable to fetch Ventrilo status.</p>";\n}\n?>