<?php\n// Create a new DOM Document\n$dom = new DOMDocument('1.0', 'UTF-8');\n\n// Add some content to the XML document\n$root = $dom->createElement('root');\n$dom->appendChild($root);\n\n$child = $dom->createElement('child', 'This is a child element.');\n$root->appendChild($child);\n\n// Specify the file path where you want to save the XML\n$filePath = '/path/to/your/directory/filename.xml';\n\n// Save the XML to the specified file path\nif ($dom->save($filePath)) {\n    echo "XML file has been saved successfully.";\n} else {\n    echo "Error saving the XML file.";\n}\n?>