<?php\n$url = "http://www.w3schools.com/PHP/func_string_str_split.asp";\n$host = parse_url($url, PHP_URL_HOST);\n\n// Use explode to split the host by dots\n$hostParts = explode('.', $host);\n\n// Check if the host has more than 2 parts (indicating a subdomain)\nif (count($hostParts) > 2) {\n    // Remove the first part (subdomain)\n    array_shift($hostParts);\n}\n\n// Join the remaining parts to get the domain\n$domain = implode('.', $hostParts);\n\necho $domain;  // Outputs "w3schools.com"\n?>