<?php\nsession_start();\n\n// Assuming you have a session variable that stores the username\n$username = $_SESSION['username'];\n\nrequire_once 'path/to/phpfreechat/src/phpfreechat.class.php';\n\n$params = array();\n$params["serverid"] = md5(__FILE__); // calculate a unique id for this chat\n$params["title"] = "My Chat Room";\n$params["nick"] = $username; // Use the logged-in user's username\n$params["admins"] = array("admin" => "adminpassword");\n$params["frozen_channels"] = array("public");\n$params["display_ping"] = true;\n$params["max_channels"] = 1;\n$params["max_privmsg"] = 10;\n\n$chat = new phpFreeChat($params);\n?>\n<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <title>Chat Room</title>\n  <?php $chat->printJavascript(); ?>\n  <?php $chat->printStyle(); ?>\n</head>\n<body>\n  <div id="chat-container">\n    <?php $chat->printChat(); ?>\n  </div>\n</body>\n</html>