public function testGetDomainFromConfigThrowsExceptionWhenDomainIsNotSet()\n{\n    // Retrieve the config object from the registry\n    $config = Zend_Registry::get('config');\n    \n    // Remove the 'domain' key from the config object\n    unset($config->domain);\n    \n    // Update the config object back in the registry\n    Zend_Registry::set('config', $config);\n\n    // Expect the exception to be thrown\n    $this->expectException(Exception::class);\n    $this->expectExceptionMessage('Please make sure you have "domain" set in your config file and your config file is being set in the Zend_Registry.');\n\n    // Call the method that should throw the exception\n    $this->getDomainFromConfig();\n}