function is_ajax_request() {\n       return isset($_SERVER['HTTP_X_REQUESTED_WITH']) && $_SERVER['HTTP_X_REQUESTED_WITH'] === 'XMLHttpRequest';\n   }\n\n   if (is_ajax_request()) {\n       // Serve the requested content for AJAX\n       if (isset($_GET['page'])) {\n           $page = $_GET['page'];\n           // Include the content of the requested page\n           include("pages/{$page}.php");\n       }\n   } else {\n       // Serve the full page for direct access/bookmark\n       ?>\n       <!DOCTYPE html>\n       <html lang="en">\n       <head>\n           <meta charset="UTF-8">\n           <meta name="viewport" content="width=device-width, initial-scale=1.0">\n           <title>My AJAXified Site</title>\n           <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>\n           <script src="path/to/your/ajax-script.js"></script>\n       </head>\n       <body>\n           <div id="content">\n               <?php\n               // Include the content of the requested page or the home page by default\n               if (isset($_GET['page'])) {\n                   $page = $_GET['page'];\n                   include("pages/{$page}.php");\n               } else {\n                   include("pages/home.php");\n               }\n               ?>\n           </div>\n       </body>\n       </html>\n       <?php\n   }