<?php\n   // Database connection\n   $conn = new mysqli('hostname', 'username', 'password', 'database');\n\n   // Check connection\n   if ($conn->connect_error) {\n       die("Connection failed: " . $conn->connect_error);\n   }\n\n   // Fetch forum threads\n   $threads = $conn->query("SELECT * FROM forum_threads");\n\n   while ($thread = $threads->fetch_assoc()) {\n       $thread_id = $thread['id'];\n       $thread_title = htmlspecialchars($thread['title'], ENT_QUOTES, 'UTF-8');\n       $thread_content = htmlspecialchars($thread['content'], ENT_QUOTES, 'UTF-8');\n\n       // Create thread HTML file\n       $thread_html = "\n       <!DOCTYPE html>\n       <html lang='en'>\n       <head>\n           <meta charset='UTF-8'>\n           <title>$thread_title</title>\n       </head>\n       <body>\n           <h1>$thread_title</h1>\n           <p>$thread_content</p>\n       </body>\n       </html>";\n\n       file_put_contents("archive/thread_$thread_id.html", $thread_html);\n   }\n\n   // Fetch user profiles\n   $users = $conn->query("SELECT * FROM user_profiles");\n\n   while ($user = $users->fetch_assoc()) {\n       $user_id = $user['id'];\n       $username = htmlspecialchars($user['username'], ENT_QUOTES, 'UTF-8');\n       $profile = htmlspecialchars($user['profile'], ENT_QUOTES, 'UTF-8');\n\n       // Create profile HTML file\n       $profile_html = "\n       <!DOCTYPE html>\n       <html lang='en'>\n       <head>\n           <meta charset='UTF-8'>\n           <title>$username's Profile</title>\n       </head>\n       <body>\n           <h1>$username's Profile</h1>\n           <p>$profile</p>\n       </body>\n       </html>";\n\n       file_put_contents("archive/profile_$user_id.html", $profile_html);\n   }\n\n   $conn->close();\n   ?>