<?php\n\n$mysqli = new mysqli("localhost", "username", "password", "db_name");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get the questions\n$query = "\n    SELECT questions.id as qid, questions.question as question\n    FROM questions;\n";\n\nif ($result = $mysqli->query($query)) {\n    $questions = [];\n    while ($row = $result->fetch_assoc()) {\n        $questions[$row['qid']] = [\n            'question' => $row['question'],\n            'answers' => []\n        ];\n    }\n    $result->free();\n}\n\n// Query to get the answers associated with each question\n$query = "\n    SELECT a_lookup.q_id as qid, answers.answer as answer\n    FROM a_lookup\n    JOIN answers ON a_lookup.a_id = answers.id;\n";\n\nif ($result = $mysqli->query($query)) {\n    while ($row = $result->fetch_assoc()) {\n        $questions[$row['qid']]['answers'][] = $row['answer'];\n    }\n    $result->free();\n}\n\n$mysqli->close();\n\n// Display the questions and their associated answers\nforeach ($questions as $qid => $qdata) {\n    echo "Question: " . $qdata['question'] . "<br/>";\n    foreach ($qdata['answers'] as $answer) {\n        echo "Answer: " . $answer . "<br/>";\n    }\n    echo "<br/>";\n}\n\n?>