function storeFile($filePath, $content) {\n    // Generate a hash for the file\n    $hash = md5($filePath);\n    \n    // Create a directory structure based on the hash\n    $directory = "uploads/" . substr($hash, 0, 2) . "/" . substr($hash, 2, 2) . "/" . substr($hash, 4, 2) . "/";\n    \n    // Ensure the directory exists\n    if (!is_dir($directory)) {\n        mkdir($directory, 0777, true);\n    }\n    \n    // Store the file\n    $fileLocation = $directory . basename($filePath);\n    file_put_contents($fileLocation, $content);\n    \n    return $fileLocation;\n}\n\n// Example usage\n$fileLocation = storeFile("example.txt", "File content here");\necho "File stored at: " . $fileLocation;