class My_Form_Store extends Zend_Form\n{\n    public function init()\n    {\n        // Create a store_id element\n        $storeId = new Zend_Form_Element_Text('store_id');\n        $storeId->setLabel('Store ID:')\n                ->setAttrib('disabled', 'disabled'); // Disable the element\n\n        // Add the store_id element to the form\n        $this->addElement($storeId);\n\n        // Other form elements go here...\n\n        // Example of an editable element\n        $storeName = new Zend_Form_Element_Text('store_name');\n        $storeName->setLabel('Store Name:')\n                  ->setRequired(true);\n\n        // Add the store_name element to the form\n        $this->addElement($storeName);\n        \n        // Add more elements as needed...\n    }\n}