<?php\nrequire_once 'Zend/Search/Lucene.php';\n\n$index = Zend_Search_Lucene::open('/path/to/your/index');\n\n$query = $_GET['query'];\n$searchType = $_GET['search_type'];\n\nif ($searchType == 'item') {\n    // Search only item fields\n    $query = 'item-title:(' . $query . ') OR item-description:(' . $query . ') OR item-tags:(' . $query . ')';\n} elseif ($searchType == 'seller') {\n    // Search only seller name field\n    $query = 'seller-name:(' . $query . ')';\n}\n\n$hits = $index->find($query);\n\nforeach ($hits as $hit) {\n    echo $hit->itemTitle . '<br>';\n    // Display other fields as needed\n}\n?>