<?php\n$index = Zend_Search_Lucene::create('/path/to/your/index');\n\n$doc = new Zend_Search_Lucene_Document();\n\n$doc->addField(Zend_Search_Lucene_Field::Keyword('item-id', $itemId));\n$doc->addField(Zend_Search_Lucene_Field::Text('item-title', $itemTitle));\n$doc->addField(Zend_Search_Lucene_Field::UnStored('item-description', $itemDescription));\n$doc->addField(Zend_Search_Lucene_Field::Text('item-tags', $itemTags));\n$doc->addField(Zend_Search_Lucene_Field::Keyword('item-price', $itemPrice));\n$doc->addField(Zend_Search_Lucene_Field::Keyword('seller-id', $sellerId));\n$doc->addField(Zend_Search_Lucene_Field::Text('seller-name', $sellerName));\n\n$index->addDocument($doc);