public static function getFeed($db)\n{\n    $title = 'Latest feeds';\n    $feedUri = '/rss/';\n    \n    // Link from which feed is available\n    $link = 'http://' . $_SERVER['HTTP_HOST'] . $feedUri;\n\n    // Create array according to structure defined in Zend_Feed documentation\n    $feedArr = array(\n        'title' => $title,\n        'link' => $link,\n        'description' => $title,\n        'language' => 'en-us',\n        'charset' => 'utf-8',\n        'generator' => 'Zend Framework Zend_Feed',\n        'entries' => array()\n    );\n\n    $itemObjs = array();\n    $select = $db->select('id')->from('things')\n        ->order('createddate desc')\n        ->limit(10);\n    $results = $db->fetchAll($select->__toString());\n\n    foreach ($results as $result) {\n        $itemObj = SiteUtil::getItemObjectInstance($db, $result['id']);\n        $feedArr['entries'][] = array(\n            'title' => $itemObj->getSummary(),\n            'link' => 'http://' . $_SERVER['HTTP_HOST'] . $itemObj->getDetailUri(),\n            'description' => $itemObj->description,\n            'pubDate' => gmdate(DATE_RSS, strtotime($itemObj->publishedDate)),\n            'guid' => 'http://' . $_SERVER['HTTP_HOST'] . $itemObj->getDetailUri()\n        );\n    }\n\n    $feed = Zend_Feed::importArray($feedArr, 'rss');\n    return $feed;\n}