<?php\n// Load JSON file\n$contents = file_get_contents("summary.json");\n$data = json_decode($contents, true);\n\n// Function to filter data based on keywords\nfunction filterData($data, $keywords) {\n    $filteredData = [];\n    foreach ($data as $item) {\n        foreach ($keywords as $keyword) {\n            if (strpos($item['keywords'], $keyword) !== false) {\n                $filteredData[] = $item;\n                break;\n            }\n        }\n    }\n    return $filteredData;\n}\n\n// Get keywords from user input (e.g., via GET request)\n$keywords = isset($_GET['keywords']) ? explode(',', $_GET['keywords']) : [];\n\n// Filter data\n$result = filterData($data, $keywords);\n\n// Display results\nforeach ($result as $item) {\n    echo "<h3>{$item['title']}</h3>";\n    echo "<p>{$item['description']}</p>";\n    echo "<p><strong>Keywords:</strong> {$item['keywords']}</p>";\n}\n?>