<?php\n// Sample data array\n$data = [\n    ['title' => 'Entry 1', 'description' => 'entry 1 description'],\n    ['title' => 'Entry 2', 'description' => 'entry 2 description'],\n    ['title' => 'Entry 3', 'description' => 'entry 3 description'],\n    ['title' => 'Entry 4', 'description' => 'entry 4 description'],\n    ['title' => 'Entry 5', 'description' => 'entry 5 description']\n];\n\n// To ensure security, sanitize the data before displaying it\nfunction sanitize($data) {\n    return htmlspecialchars($data, ENT_QUOTES, 'UTF-8');\n}\n\necho '<table>';\nfor ($i = 0; $i < count($data); $i += 2) {\n    echo '<tr>';\n    // First column\n    echo '<td>';\n    echo '<strong>' . sanitize($data[$i]['title']) . '</strong><br>';\n    echo sanitize($data[$i]['description']);\n    echo '</td>';\n\n    // Second column (if exists)\n    if (isset($data[$i + 1])) {\n        echo '<td>';\n        echo '<strong>' . sanitize($data[$i + 1]['title']) . '</strong><br>';\n        echo sanitize($data[$i + 1]['description']);\n        echo '</td>';\n    }\n    echo '</tr>';\n}\necho '</table>';\n?>