class MyController extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        // Load the model once in the constructor\n        $this->load->model('my_model');\n    }\n\n    public function some_method() {\n        // You can now use $this->my_model without needing to load it again\n        $data = $this->my_model->get_data();\n        // ...\n    }\n\n    public function another_method() {\n        // You can still use $this->my_model here as well\n        $result = $this->my_model->process_data();\n        // ...\n    }\n}