function connectSugarCRM()\n{\n    $servername = "localhost";\n    $username = "123";\n    $password = "123";\n    $dbname = "sugar5";\n\n    // Create connection\n    $conn = new mysqli($servername, $username, $password, $dbname);\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    return $conn;\n}\n\n// Ensure the function is not redeclared\nif (!function_exists('connectSugarCRM')) {\n    function connectSugarCRM()\n    {\n        $servername = "localhost";\n        $username = "123";\n        $password = "123";\n        $dbname = "sugar5";\n\n        // Create connection\n        $conn = new mysqli($servername, $username, $password, $dbname);\n\n        // Check connection\n        if ($conn->connect_error) {\n            die("Connection failed: " . $conn->connect_error);\n        }\n\n        return $conn;\n    }\n}\n\n// Running a query\n$conn = connectSugarCRM();\n$sqlSTR = "YOUR_SQL_QUERY_HERE";\n$result = $conn->query($sqlSTR);\n\nif ($result === FALSE) {\n    die("Query Failed: " . $conn->error);\n}\n\n// Process the result\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        // Do something with $row\n    }\n}\n\n$conn->close();