public function sortedPhotogsByLocation($location)\n{\n    $q = new Doctrine_RawSql();\n    $result = $q->select('{p.*}')\n            ->from('photographers p')\n            ->addComponent('p', 'Photographer')\n            ->where('p.city_id = ?', $location->id)\n            ->orderBy('CASE WHEN p.lname < :sort THEN 1 ELSE 0 END, p.lname ASC')\n            ->execute(array(':sort' => $location->photographer_sort));\n    return $result;\n}