function sanitizeFileName($filename) {\n    // List of characters to remove\n    $invalidCharacters = array(\n        '/', '\\', ':', '*', '?', '"', '<', '>', '|', '.'\n    );\n\n    // Replace each invalid character with an underscore\n    $sanitized = str_replace($invalidCharacters, '_', $filename);\n\n    // Optionally, trim any trailing or leading whitespace\n    $sanitized = trim($sanitized);\n\n    return $sanitized;\n}\n\n// Example usage\n$originalFileName = "example:invalid/file*name?.txt";\n$sanitizedFileName = sanitizeFileName($originalFileName);\necho $sanitizedFileName;  // Outputs: example_invalid_file_name_.txt