<?php\n\nfunction proxy_request($url) {\n    $ch = curl_init();\n    \n    // Set the URL\n    curl_setopt($ch, CURLOPT_URL, $url);\n    \n    // Pass headers received from the client to the target server\n    $headers = array();\n    foreach (getallheaders() as $name => $value) {\n        $headers[] = "$name: $value";\n    }\n    curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);\n    \n    // Handle cookies\n    curl_setopt($ch, CURLOPT_COOKIE, $_SERVER['HTTP_COOKIE']);\n    \n    // Forward the request method (GET, POST, etc.)\n    switch ($_SERVER['REQUEST_METHOD']) {\n        case 'POST':\n            curl_setopt($ch, CURLOPT_POST, true);\n            curl_setopt($ch, CURLOPT_POSTFIELDS, file_get_contents('php://input'));\n            break;\n        case 'PUT':\n            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "PUT");\n            curl_setopt($ch, CURLOPT_POSTFIELDS, file_get_contents('php://input'));\n            break;\n        case 'DELETE':\n            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "DELETE");\n            break;\n        default:\n            curl_setopt($ch, CURLOPT_HTTPGET, true);\n            break;\n    }\n    \n    // Return the response as a string\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Execute the request\n    $response = curl_exec($ch);\n    \n    // Forward the response headers\n    foreach (explode("\r\n", curl_getinfo($ch, CURLINFO_HEADER_OUT)) as $header) {\n        if (strpos($header, ':') !== false) {\n            header($header);\n        }\n    }\n    \n    // Close the cURL session\n    curl_close($ch);\n    \n    // Return the response\n    return $response;\n}\n\n// The URL you want to proxy\n$url = "http://example.com" . $_SERVER['REQUEST_URI'];\n\n// Output the proxied content\necho proxy_request($url);\n\n?>