// Insert into MySQL\n$binary_ipv6 = inet_pton('2001:0db8:85a3:0000:0000:8a2e:0370:7334');\n$stmt = $pdo->prepare("INSERT INTO ipv6_addresses (ipv6_address) VALUES (?)");\n$stmt->execute([$binary_ipv6]);\n\n// Retrieve from MySQL\n$stmt = $pdo->query("SELECT ipv6_address FROM ipv6_addresses WHERE id = 1");\n$row = $stmt->fetch(PDO::FETCH_ASSOC);\n$ipv6_address = inet_ntop($row['ipv6_address']);\necho $ipv6_address; // Outputs the original IPv6 address