function customMail($to, $subject, $message, $headers = '', $parameters = '') {\n    // Parse headers\n    $headersArray = explode("\r\n", $headers);\n    $from = 'from@example.com'; // Default from address\n    foreach ($headersArray as $header) {\n        if (strpos($header, 'From:') === 0) {\n            $from = trim(str_replace('From:', '', $header));\n        }\n    }\n\n    sendEmail($to, $subject, $message, $from);\n}\n\n// Replace all calls to mail() with customMail() in your codebase