function createExcerpt($text, $searchTerm) {\n    $pos = stripos($text, $searchTerm);\n    if ($pos !== false) {\n        $start = max(0, $pos - 20);\n        $length = strlen($searchTerm) + 50;\n        $excerpt = substr($text, $start, $length);\n        $excerpt = '...' . $excerpt . '...';\n        return str_ireplace($searchTerm, '<b>' . $searchTerm . '</b>', $excerpt);\n    }\n    return $text;\n}\n\nforeach ($results as &$result) {\n    $result['excerpt'] = createExcerpt($result['content'], $_GET['search']);\n}