// Function to scan file for viruses\nfunction scanFileForVirus($filePath) {\n    $safePath = escapeshellarg($filePath);\n    $command = '/usr/bin/clamscan --stdout ' . $safePath;\n    $output = [];\n    $returnCode = -1;\n    \n    exec($command, $output, $returnCode);\n\n    if ($returnCode === 0) {\n        return true; // No virus found\n    } else {\n        return false; // Virus detected\n    }\n}\n\n// Example usage\n$dir = '/path/to/uploads/';\n$file = 'uploaded_file.txt';\n$safePath = $dir . $file;\n\nif (scanFileForVirus($safePath)) {\n    echo "File is clean.";\n} else {\n    echo "VIRUS DETECTED!";\n}