<?php\n// URL of the external XML file\n$xmlUrl = "http://example.com/path-to-your-xml-file.xml";\n\n// Load the XML file\n$xmlContent = simplexml_load_file($xmlUrl);\n\nif ($xmlContent === false) {\n    echo "Failed to load XML file.";\n    foreach(libxml_get_errors() as $error) {\n        echo "<br>", $error->message;\n    }\n} else {\n    // Iterate through the XML and display the contents\n    foreach($xmlContent->child as $child) {\n        echo $child->grandchild . "<br>";\n    }\n}\n?>