function RandomSite($intNumberofSites = 1) {\n    $arrOutput = [];\n\n    // Open the database connection (assuming GetDatabaseConnection is defined elsewhere)\n    GetDatabaseConnection('dev');\n\n    // Step 1: Get a random set of IDs\n    $strSQL = "\n        SELECT id\n        FROM site_info\n        WHERE major <> 0\n        ORDER BY RAND()\n        LIMIT " . intval($intNumberofSites) . ";";\n    \n    $result = @mysql_query($strSQL);\n    \n    if ($result) {\n        while ($row = mysql_fetch_array($result, MYSQL_NUM)) {\n            $arrOutput[] = $row[0];\n        }\n    }\n\n    return $arrOutput;\n}