function RandomSite($intNumberofSites = 1) {\n    $arrOutput = [];\n\n    // Open the database connection using mysqli\n    $conn = new mysqli('localhost', 'username', 'password', 'database');\n    \n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Step 1: Get a random set of IDs\n    $strSQL = "\n        SELECT id\n        FROM site_info\n        WHERE major <> 0\n        ORDER BY RAND()\n        LIMIT " . intval($intNumberofSites) . ";";\n    \n    $result = $conn->query($strSQL);\n    \n    if ($result) {\n        while ($row = $result->fetch_assoc()) {\n            $arrOutput[] = $row['id'];\n        }\n    }\n\n    $conn->close();\n\n    return $arrOutput;\n}