function input_lab_results($name, $image, $descrip) {\n    $dsn = 'mysql:host=your_host;dbname=your_dbname';\n    $username = 'your_username';\n    $password = 'your_password';\n\n    try {\n        $pdo = new PDO($dsn, $username, $password);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        \n        $query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (:name, :image, :descrip, :doctor, :resident, :create, :modify)";\n        $stmt = $pdo->prepare($query);\n        \n        $stmt->execute([\n            ':name' => $name,\n            ':image' => $image,\n            ':descrip' => $descrip,\n            ':doctor' => 0,\n            ':resident' => 0,\n            ':create' => date("Ymd"),\n            ':modify' => date("Ymd")\n        ]);\n        \n    } catch (PDOException $e) {\n        die("Error: " . $e->getMessage());\n    }\n}