ini_set('memory_limit', '256M'); // Increase as needed\nini_set('max_execution_time', '300'); // Increase as needed\n\nheader("Pragma: public");\nheader("Cache-Control: must-revalidate, post-check=0, pre-check=0");\nheader("Cache-Control: private", false);\nheader("Content-type: application/force-download");\nheader("Content-Disposition: attachment; filename=\"" . $dname . ".mp3\";");\nheader("Content-Transfer-Encoding: binary");\nheader("Content-Length: " . filesize($secretfile));\n\nflush(); // Flush system output buffer\n\n$handle = fopen($secretfile, "rb");\nif ($handle === false) {\n    header("HTTP/1.1 500 Internal Server Error");\n    exit();\n}\nwhile (!feof($handle)) {\n    print(fread($handle, 8192)); // Output the file in chunks\n    flush(); // Flush system output buffer after each chunk\n}\nfclose($handle);