function input_lab_results($name, $image, $descrip) {\n    $query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (?, ?, ?, ?, ?, ?, ?)";\n    \n    if ($stmt = $this->conn->prepare($query)) {\n        $doctor = 0;\n        $resident = 0;\n        $createDate = date("Ymd");\n        $modifyDate = date("Ymd");\n        \n        $stmt->bind_param('sssiiis', $name, $image, $descrip, $doctor, $resident, $createDate, $modifyDate);\n        $stmt->execute();\n    } else {\n        die($this->conn->error);\n    }\n}