$user_id = $userdata['user_id']; // Get the current user's ID\n\n$sql = "SELECT g.group_id\n        FROM " . USER_GROUP_TABLE . " ug, " . GROUPS_TABLE . " g\n        WHERE ug.user_id = $user_id\n        AND g.group_id = ug.group_id\n        AND g.group_single_user = 0";\n\nif (!($result = $db->sql_query($sql))) {\n    message_die(GENERAL_ERROR, 'Could not obtain user group information', '', __LINE__, __FILE__, $sql);\n}\n\n$allowed_group_id = 2; // Replace with the ID of the group you want to allow access\n\n$user_in_group = false;\nwhile ($row = $db->sql_fetchrow($result)) {\n    if ($row['group_id'] == $allowed_group_id) {\n        $user_in_group = true;\n        break;\n    }\n}\n\nif (!$user_in_group) {\n    die('You do not have permission to access this page.');\n}