<?php\n$serverName = "your_server_name";\n$connectionOptions = array(\n    "Database" => "your_database_name",\n    "Uid" => "your_username",\n    "PWD" => "your_password"\n);\n\n// Establishes the connection\n$conn = sqlsrv_connect($serverName, $connectionOptions);\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$perPage = 10; // Number of records to display per page\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1; // Current page number\n$offset = ($page - 1) * $perPage; // Calculate the offset\n\n$sql = "SELECT * FROM your_table_name ORDER BY your_column_name OFFSET ? ROWS FETCH NEXT ? ROWS ONLY";\n\n$params = array($offset, $perPage);\n\n$stmt = sqlsrv_query($conn, $sql, $params);\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Fetch results and display them\nwhile ($row = sqlsrv_fetch_array($stmt, SQLSRV_FETCH_ASSOC)) {\n    echo $row['your_column_name'] . "<br>";\n}\n\n// Generate pagination links\n$totalSql = "SELECT COUNT(*) as total FROM your_table_name";\n$totalStmt = sqlsrv_query($conn, $totalSql);\n$totalRow = sqlsrv_fetch_array($totalStmt, SQLSRV_FETCH_ASSOC);\n$totalRecords = $totalRow['total'];\n$totalPages = ceil($totalRecords / $perPage);\n\necho "<div class='pagination'>";\nfor ($i = 1; $i <= $totalPages; $i++) {\n    echo "<a href='?page=$i'>$i</a> ";\n}\necho "</div>";\n\nsqlsrv_free_stmt($stmt);\nsqlsrv_close($conn);\n?>