// Incrementing Variables\n$i = 0;\n++$i; // Pre-increment\n$i++; // Post-increment\n\n// Output Operations\necho "Hello, World!"; // Faster\nprintf("Hello, %s!", "World"); // Slower\n\n// Function Calls\nfunction directFunction() {\n    return "Direct function call";\n}\n\nclass MyClass {\n    public function objectMethod() {\n        return "Object method call";\n    }\n}\n\n$object = new MyClass();\necho directFunction(); // Faster\necho $object->objectMethod(); // Slower\n\n// Variable Scope\n$globalVar = "Global";\nfunction testScope() {\n    $localVar = "Local"; // Faster\n    global $globalVar; // Slower\n}\n\n// Array vs. Direct Data Access\n$array = [1, 2, 3];\necho $array[0]; // Array access\n$data = 1;\necho $data; // Direct data access\n\n// Database Operations (Assuming a MySQL connection is established)\n$result = mysql_query("SELECT * FROM table");\n$rowAssoc = mysql_fetch_assoc($result); // Slower\n$rowNum = mysql_fetch_row($result); // Faster