try {\n    $select = $this->_db->select()\n        ->from(array('oslang' => 'oslang'), array('oslang'))  // Select from OSLANG table\n        ->join(array('os' => 'os'), 'oslang.oslang_id = os.oslang_id')  // Join with OS table\n        ->where('os.ossubversion_id = ?', $subVersionId);  // Condition on OSSUBVERSION_ID\n    \n    $results = $select->query()->fetchAll();\n} catch (Exception $e) {\n    $this->_logException($e);\n    $results = array();\n}