function isServerBusy($loadThreshold = 5, $queryThreshold = 10) {\n    // Get system load averages\n    $load = sys_getloadavg();\n\n    // Check the 1-minute load average\n    if ($load[0] > $loadThreshold) {\n        return true;\n    }\n\n    // Connect to MySQL\n    $mysqli = new mysqli('localhost', 'username', 'password', 'database');\n\n    if ($mysqli->connect_error) {\n        die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n    }\n\n    // Query the process list\n    $result = $mysqli->query("SHOW FULL PROCESSLIST");\n    if ($result) {\n        $queryCount = $result->num_rows;\n        $result->free();\n        $mysqli->close();\n\n        // Check if the number of queries exceeds the threshold\n        if ($queryCount > $queryThreshold) {\n            return true;\n        }\n    } else {\n        $mysqli->close();\n        die('Query Error: ' . $mysqli->error);\n    }\n\n    return false;\n}\n\n// Usage example\nif (!isServerBusy()) {\n    // Run your cron job\n    echo "Server is not busy, running cron job...";\n} else {\n    echo "Server is busy, skipping cron job...";\n}