<?php\n   $count_my_page = __DIR__ . "/hitcounter.txt"; // Use absolute path\n   if (file_exists($count_my_page)) {\n       $hits = file($count_my_page);\n       $hits[0]++;\n       $fp = fopen($count_my_page, "w");\n       if ($fp) {\n           fputs($fp, "$hits[0]");\n           fclose($fp);\n       }\n       // Your existing code for random number selection\n       if ($hits[0] <= 1)\n           $random_number = 0;\n       else if ($hits[0] > 1 && $hits[0] <= 2)\n           $random_number = 1;\n       else if ($hits[0] > 2 && $hits[0] <= 3)\n           $random_number = 2;\n       else if ($hits[0] > 3 && $hits[0] <= 4)\n           $random_number = 3;\n       else if ($hits[0] > 4 && $hits[0] <= 5)\n           $random_number = 4;\n       else if ($hits[0] > 5 && $hits[0] <= 6)\n           $random_number = 5;\n       else if ($hits[0] > 6 && $hits[0] <= 7)\n           $random_number = 6;\n       else if ($hits[0] > 7 && $hits[0] <= 8)\n           $random_number = 7;\n       else if ($hits[0] > 8 && $hits[0] <= 9) {\n           $random_number = 8;\n           if ($hits[0] == 9) {\n               $fp = fopen($count_my_page, "w");\n               $hits[0] = 0;\n               fputs($fp, "$hits[0]");\n               fclose($fp);\n           }\n       }\n   } else {\n       // Handle error if file does not exist\n       echo "Error: hitcounter.txt file does not exist.";\n   }\n   ?>