function splitTags($text) {\n    // Split the text by spaces and commas while allowing -, _, +, and #\n    $tags = preg_split('/[\s,]+/', $text);\n\n    // Filter out any empty tags that may result from multiple spaces or commas\n    $tags = array_filter($tags, function($tag) {\n        return !empty($tag);\n    });\n\n    return $tags;\n}\n\n$text = "tag1, tag2 tag3-tag4_tag5+tag6#tag7";\n$tags = splitTags($text);\n\nprint_r($tags);