function decimal_to_inet_ntop($decimal) {\n    $hex = base_convert($decimal, 10, 16);\n    $hex = str_pad($hex, 32, '0', STR_PAD_LEFT); // Ensure the hex string is 32 characters long\n    $binary = pack('H*', $hex);\n    return inet_ntop($binary);\n}\n\n// Example usage\n$binary_ipv6 = decimal_to_inet_ntop($decimal_ipv6);\necho inet_ntop($binary_ipv6); // Outputs the original IPv6 address