<?php\nfunction findNodesWithClass(SimpleXMLElement $element, $className, &$result = []) {\n    // Check if the current element has the specified class attribute\n    if (isset($element['class']) && (string)$element['class'] === $className) {\n        $result[] = $element;\n    }\n    \n    // Recursively check each child element\n    foreach ($element->children() as $child) {\n        findNodesWithClass($child, $className, $result);\n    }\n}\n\n// Load the XHTML document\n$xmlString = file_get_contents('path/to/your/document.xhtml');\n$xml = simplexml_load_string($xmlString);\n\n// Define the class name to search for\n$className = 'your-class-name';\n\n// Find all nodes with the specified class\n$result = [];\nfindNodesWithClass($xml, $className, $result);\n\n// Output the results\nforeach ($result as $node) {\n    echo $node->asXML() . "\n";\n}\n?>