<?php\n    function convertMarkdownToHtml($markdown) {\n        // Path to the Node.js script\n        $script = '/path/to/converter.js';\n\n        // Use shell_exec to call Node.js and pass the markdown through stdin\n        $cmd = "echo " . escapeshellarg($markdown) . " | node " . escapeshellarg($script);\n        $html = shell_exec($cmd);\n\n        return $html;\n    }\n\n    // Example usage\n    $markdown = "# Hello World\nThis is a markdown text.";\n    $html = convertMarkdownToHtml($markdown);\n\n    echo $html;\n    ?>