<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (isset($_POST['availability']) && is_array($_POST['availability'])) {\n        $availability = $_POST['availability'];\n\n        // Process the availability array as needed\n        foreach ($availability as $day) {\n            echo htmlspecialchars($day) . "<br>";\n        }\n\n        // To check if a specific time slot is available\n        if (in_array('MondayMorning', $availability)) {\n            echo "You are available on Monday morning.<br>";\n        }\n    } else {\n        echo "No availability selected.<br>";\n    }\n}\n?>