function getFirstWords($string, $wordLimit = 50) {\n    // Split the string into an array of words\n    $wordsArray = explode(' ', $string);\n    \n    // Get the first 50 words\n    $firstWordsArray = array_slice($wordsArray, 0, $wordLimit);\n    \n    // Join the words back into a string\n    $resultString = implode(' ', $firstWordsArray);\n\n    return $resultString;\n}\n\n// Example usage\n$text = "Your long block of text goes here...";\n$first50Words = getFirstWords($text, 50);\n\necho $first50Words;