class Image {\n    private $src;\n    private $width;\n    private $height;\n\n    public function __construct($src, $width, $height) {\n        $this->src = $src;\n        $this->width = $width;\n        $this->height = $height;\n    }\n\n    public static function thumbnail($imagePath, $size) {\n        // Implement the logic to generate a thumbnail\n        return [\n            'src' => $imagePath,\n            'width' => $size,\n            'height' => $size\n        ];\n    }\n}\n\n// Usage\n$image = new Image($imagePath, 200, 200);\n$thumbnail = Image::thumbnail($imagePath, 200);\n$imgHtml = '<img alt="' . $this->getTitle() . '" src="' . $thumbnail['src'] . '" width="' . $thumbnail['width'] . '" height="' . $thumbnail['height'] . '" />';