public function insert()\n{\n    // Prepare data and placeholders for the SQL statement\n    $params = $this->data;\n    $columns = array_keys($params);\n    $placeholders = array_fill(0, count($params), '?');\n    \n    // Create the SQL statement\n    $sql = sprintf(\n        "INSERT INTO ItemMaster (%s) VALUES (%s)",\n        implode(", ", $columns),\n        implode(", ", $placeholders)\n    );\n\n    // Initialize and prepare the statement\n    $stmt = $this->db->stmt_init();\n    if (!$stmt->prepare($sql)) {\n        die("Could not prepare statement: " . $stmt->error);\n    }\n\n    // Create an array of references to the data values\n    $values = array_values($params);\n    $types = str_repeat('s', count($values)); // Assuming all fields are strings\n\n    // Bind the parameters to the statement\n    $stmt->bind_param($types, ...$values);\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        return true;\n    } else {\n        error_log("Insert failed: " . $stmt->error);\n        return false;\n    }\n}