require "vendor/autoload.php";\nuse Abraham\TwitterOAuth\TwitterOAuth;\n\n// Step 1: Redirect user to Twitter for authorization\n$consumerKey = 'your-consumer-key';\n$consumerSecret = 'your-consumer-secret';\n$callbackUrl = 'your-callback-url';\n\n$connection = new TwitterOAuth($consumerKey, $consumerSecret);\n$requestToken = $connection->oauth('oauth/request_token', ['oauth_callback' => $callbackUrl]);\n\n// Store the request token in session\nsession_start();\n$_SESSION['oauth_token'] = $requestToken['oauth_token'];\n$_SESSION['oauth_token_secret'] = $requestToken['oauth_token_secret'];\n\n$authUrl = $connection->url('oauth/authorize', ['oauth_token' => $requestToken['oauth_token']]);\nheader('Location: ' . $authUrl);\n\n// Step 2: Get access token after user authorizes the application\nsession_start();\n$oauthToken = $_SESSION['oauth_token'];\n$oauthTokenSecret = $_SESSION['oauth_token_secret'];\n\n$connection = new TwitterOAuth($consumerKey, $consumerSecret, $oauthToken, $oauthTokenSecret);\n$accessToken = $connection->oauth("oauth/access_token", ["oauth_verifier" => $_GET['oauth_verifier']]);\n\n// Store the access token securely in your database\n$userId = $accessToken['user_id'];\n$screenName = $accessToken['screen_name'];\n$oauthToken = $accessToken['oauth_token'];\n$oauthTokenSecret = $accessToken['oauth_token_secret'];\n\n// Example: Store in database (use prepared statements and encryption in a real application)\n$db = new PDO('mysql:host=localhost;dbname=yourdbname', 'username', 'password');\n$stmt = $db->prepare("INSERT INTO twitter_tokens (user_id, screen_name, oauth_token, oauth_token_secret) VALUES (?, ?, ?, ?)");\n$stmt->execute([$userId, $screenName, $oauthToken, $oauthTokenSecret]);\n\n// Step 3: Make authenticated API calls using access token\n$connection = new TwitterOAuth($consumerKey, $consumerSecret, $oauthToken, $oauthTokenSecret);\n$tweets = $connection->get("statuses/user_timeline", ["screen_name" => $screenName, "count" => 10]);\n\nforeach ($tweets as $tweet) {\n    echo $tweet->text . "<br>";\n}